protocol InternationalBusinessLogic {
    func doCallAtPhone(request: International.CallAtPhone.Request)
}

protocol InternationalDataStore {
    //var myDataStoreVar: String { get set }
}

class InternationalInteractor: InternationalBusinessLogic, InternationalDataStore {
    var worker: EmergencyWorker?
    
    // MARK: Do something
    func doCallAtPhone(request: International.CallAtPhone.Request) {
        if worker == nil {
            worker = EmergencyWorker()
        }
        
        worker?.callAtPhone(assistancePhone: request.assistancePhone)
    }
}
