protocol EmergencyPresentationLogic {
    func emergencyPetition(response: Emergency.Emergency.Response)
}

class EmergencyPresenter: EmergencyPresentationLogic {
    weak var viewController: EmergencyDisplayLogic?
    
    // MARK: Do something
    func emergencyPetition(response: Emergency.Emergency.Response) {
        let viewModel = Emergency.Emergency.ViewModel(isSuccess: response.isSuccess)
        viewController?.displayEmergencyPetition(viewModel: viewModel)
    }
}

