import UIKit

@objc protocol CheckCodeRoutingLogic {
    func goToMenu()
    func goToRenewPasswordViewController()
}

protocol CheckCodeDataPassing {
    var dataStore: CheckCodeDataStore? { get set }
}

class CheckCodeRouter: NSObject, CheckCodeRoutingLogic, CheckCodeDataPassing {
    weak var viewController: CheckCodeViewController?
    var dataStore: CheckCodeDataStore?
    
    // MARK: Routing
    
    func goToMenu() {
        UIStoryboard.loadMenu()
    }
    
    func goToRenewPasswordViewController() {
        let storyBoard = UIStoryboard.recoverPassword()
        guard let renewPasswordVC = storyBoard.instantiateViewController(withIdentifier: "RenewPasswordViewController") as? RenewPasswordViewController else { return }
        renewPasswordVC.router?.dataStore?.phoneNumber = dataStore?.phoneNumber ?? ""
        renewPasswordVC.router?.dataStore?.smsCode = dataStore?.smsCode ?? ""
        renewPasswordVC.router?.dataStore?.serverTimestamp = dataStore?.serverTimestamp
        viewController?.show(renewPasswordVC, sender: nil)
    }
}
