protocol WelcomeMenuSceneBusinessLogic {
    func loadUser()
}

protocol WelcomeMenuSceneDataStore {
    //var myDataStoreVar: String { get set }
}

class WelcomeMenuSceneInteractor: WelcomeMenuSceneBusinessLogic, WelcomeMenuSceneDataStore {
    var presenter: WelcomeMenuScenePresentationLogic?
    var worker: WelcomeMenuSceneWorker?
    
    // MARK: Do something
    
    func loadUser() {
        worker = WelcomeMenuSceneWorker()
        if let response = worker?.getUser() {
            self.presenter?.presentUser(response: response)
        }
    }
}
