protocol WelcomeMenuScenePresentationLogic {
    func presentUser(response: WelcomeMenuScene.LoadUser.Response)
}

class WelcomeMenuScenePresenter: WelcomeMenuScenePresentationLogic {
    weak var viewController: WelcomeMenuSceneDisplayLogic?
    
    // MARK: Do something
    
    func presentUser(response: WelcomeMenuScene.LoadUser.Response) {
        let viewModel = WelcomeMenuScene.LoadUser.ViewModel(name: response.name, date: response.date)
        viewController?.displayInfoUser(viewModel: viewModel)
    }
}
