protocol AboutBusinessLogic {
    
    func sendMail(_ request: About.SendMail.Request)
    
}

class AboutInteractor: AboutBusinessLogic{
    var presenter: AboutPresentationLogic = AboutPresenter()
    
    func sendMail(_ request: About.SendMail.Request) {
        self.presenter.presentMailComposeVC(request: request)
    }
    
    
}
