import MessageUI

protocol AboutPresentationLogic {
    func presentMailComposeVC(request: About.SendMail.Request)
}

class AboutPresenter: AboutPresentationLogic {
    weak var viewController: AboutViewController?
    
    // MARK: Do something
    
    func presentMailComposeVC(request: About.SendMail.Request) {
        
        let mailComposeVC = MFMailComposeViewController()
        mailComposeVC.setToRecipients([LocalizableKeys.Profile.About.supportEmail])
        let modelView = About.SendMail.ViewModel.init(mailComposeViewController: mailComposeVC)
        
        viewController?.displayMailCompose(viewModel: modelView)
        
        
    }
    
}
