enum SelectContactFromDevice {
    // MARK: Use cases
    
    enum GetContacts {
        struct Response {
            var contacts: [Contact]
        }
        struct ViewModel {
            var contacts: [Contact]
        }
    }
    
    enum GroupContactsByAlphabet {
        struct Request {
            var contacts: [Contact]
        }
        
        struct Response {
            var groupContacts: [String: [Contact]]
        }
        struct ViewModel {
            var groupContacts: [String: [Contact]]
        }
    }
    
    enum AddemergencyContact {
        struct Request {
            var contact: Contact
        }
        
        struct Response {
            var noDataContact: NoDataContact
        }
        
        struct ViewModel {
            var errorMessage: String
        }
    }
    
    enum GroupContactsFilteredBySearchText {
        struct Request {
            var searchText: String
        }
        
        struct Response {
            var groupContacts: [String: [Contact]]
        }
        struct ViewModel {
            var groupContacts: [String: [Contact]]
        }
    }
}
