import UIKit

@objc protocol PolicyCertificateRoutingLogic {
    func routeToShare()
}

protocol PolicyCertificateDataPassing {
    var dataStore: PolicyCertificateDataStore? { get }
}

class PolicyCertificateRouter: NSObject, PolicyCertificateRoutingLogic, PolicyCertificateDataPassing {
    weak var viewController: PolicyCertificateViewController?
    var dataStore: PolicyCertificateDataStore?
    
    // MARK: Routing
    
    func routeToShare() {
        guard let content = dataStore?.filePath else { return }
        
        let activity = UIActivityViewController(activityItems: [content], applicationActivities: nil)
        
        navigateToShare(source: self.viewController, destination: activity)
    }
    func navigateToShare(source: PolicyCertificateViewController?, destination: UIActivityViewController) {
        destination.popoverPresentationController?.sourceView = source?.view
        source?.present(destination, animated: true, completion: nil)
    }
}
