protocol PolicySelectionBeneficiariesToWalletPresentationLogic {
    func presentBeneficiares(response: PolicySelectionBeneficiariesToWallet.GetBeneficiares.Response)
    func presentShowWallet()
    func presentError()
}

class PolicySelectionBeneficiariesToWalletPresenter: PolicySelectionBeneficiariesToWalletPresentationLogic {
    weak var viewController: PolicySelectionBeneficiariesToWalletDisplayLogic?
    
    // MARK: Do something
    func presentBeneficiares(response: PolicySelectionBeneficiariesToWallet.GetBeneficiares.Response) {
        let viewModel = PolicySelectionBeneficiariesToWallet.GetBeneficiares.ViewModel(beneficiares: response.beneficiares)
        self.viewController?.displayGetBeneficiares(viewModel: viewModel)
    }
    
    func presentShowWallet() {
        self.viewController?.displayShowWallet()
    }
    
    func presentError() {
        self.viewController?.displayError(message: LocalizableKeys.Profile.PolicyDetail.notGetInsured)
    }
}
