import UIKit

@objc protocol CheckPolicyRoutingLogic {
    func checkPolicyToForm()
    func checkPolicyToExamplePolicyInd()
    func checkPolicyToExamplePolicyCol ()
}

protocol CheckPolicyDataPassing {
    var dataStore: CheckPolicyDataStore? { get }
}

class CheckPolicyRouter: NSObject, CheckPolicyRoutingLogic, CheckPolicyDataPassing {
    weak var viewController: CheckPolicyViewController?
    var dataStore: CheckPolicyDataStore?
    // MARK: Routing
    
    func checkPolicyToForm() {
       let storyboard = UIStoryboard.register()
       let formVC = storyboard.instantiateViewController(withIdentifier: "RegisterFormViewController") as! FormViewController
        formVC.router?.dataStore?.policy = dataStore?.policy
        formVC.router?.dataStore?.certificate = dataStore?.certificate
        viewController?.show(formVC, sender: nil)
   }
    
    func checkPolicyToExamplePolicyInd () {
        let story = UIStoryboard.registerCheck()
        let example = story.instantiateViewController(withIdentifier: "ExamplePolicytype") as! ExamplePolicyViewController
        example.type = .individual 
        viewController?.present(example, animated: true, completion: nil)
    }
    
    func checkPolicyToExamplePolicyCol () {
        let story = UIStoryboard.registerCheck()
        let example = story.instantiateViewController(withIdentifier: "ExamplePolicytype") as! ExamplePolicyViewController
        example.type = .collective
        viewController?.present(example, animated: true, completion: nil)
    }
    
}
