import Lottie
import AVKit

class SplashWorker {
    
    func loadAnimationLogo(completionHandler: @escaping (LOTAnimationView) -> Void) {
        let animation = LOTAnimationView.init(name: "splashLogo")
        completionHandler(animation)
    }
    
    func loadLocalVideo(completionHandler: @escaping (AVPlayer) -> Void) {
        let videoURL = Bundle.main.url(forResource: "dummy", withExtension: "mp4")
        let avPlayer = AVPlayer(url: videoURL! as URL)
        completionHandler(avPlayer)
    }
    
}
