class WelcomeMenuSceneWorker {
    func getUser() -> WelcomeMenuScene.LoadUser.Response? {
        guard let userInfo = UserDefaultsManager.shared.userInfo else { return nil }
        let response = WelcomeMenuScene.LoadUser.Response(name: userInfo.fullName, date: userInfo.lastSession ?? "")
        return response
    }
}
